package com.example.moviebrowser.ui.movieListimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.navigation.Navigationimport androidx.navigation.findNavControllerimport androidx.recyclerview.widget.RecyclerViewimport com.example.moviebrowser.Rimport com.example.moviebrowser.dataModels.TopRatedMoviesimport com.example.moviebrowser.databinding.ItemMovieBindingclass MoviesListAdapter(private val list: List<TopRatedMovies>) :    RecyclerView.Adapter<MoviesListAdapter.MoviesViewHolder>() {    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): MoviesViewHolder {        val layoutInflater = LayoutInflater.from(parent.context)        val itemBinding = ItemMovieBinding.inflate(layoutInflater, parent, false)        return MoviesViewHolder(itemBinding)    }    override fun onBindViewHolder(holder: MoviesViewHolder, position: Int) {        holder.bindData(list[position])    }    override fun getItemCount(): Int {        return list.size    }    inner class MoviesViewHolder(itemView: ItemMovieBinding) :        RecyclerView.ViewHolder(itemView.root) {        private val mItemView = itemView        fun bindData(data: TopRatedMovies) {            mItemView.mDataModel = data            mItemView.root.setOnClickListener {                val directions =                    MovieListFragmentDirections.actionMovieListFragmentToMovieDetailFragment(data)                it.findNavController().navigate(directions)            }        }    }    }